import { IRealmServer, RealmServer } from '../';
import * as GroupsModel from './group-model';

(async () => {
    const realmServer = new RealmServer({
        host: 'kiotp_nats_main_server',
        port: 9765,
        token: 'keus-iot-platform',
        serverId: 'Keus-homeId',
        storageFolder: __dirname + '/storage'
    });

    await realmServer.start();
    await realmServer.addLocalRealm(GroupsModel);
})();