export class DuplicateRealm extends Error {  
    constructor () {
        super("Duplicate Realm")
        Error.captureStackTrace(this, this.constructor);

        this.name = this.constructor.name
    }  
}

export class RealmNotFound extends Error {  
    constructor () {
        super("Realm Not Found")
        Error.captureStackTrace(this, this.constructor);

        this.name = this.constructor.name
    }
}

